// Find all elements that overflow the viewport width
const viewportWidth = document.documentElement.clientWidth;
const overflowingElements = [];

document.querySelectorAll('*').forEach(el => {
  const rect = el.getBoundingClientRect();
  if (rect.right > viewportWidth + 1 || rect.left < -1) {
    const styles = window.getComputedStyle(el);
    overflowingElements.push({
      tag: el.tagName,
      id: el.id,
      class: el.className?.toString?.()?.substring(0, 100),
      left: Math.round(rect.left),
      right: Math.round(rect.right),
      width: Math.round(rect.width),
      overflow: styles.overflow,
      overflowX: styles.overflowX,
      viewportWidth
    });
  }
});

// Also check the full scrollable width
const scrollInfo = {
  documentScrollWidth: document.documentElement.scrollWidth,
  bodyScrollWidth: document.body.scrollWidth,
  viewportWidth,
  overflowAmount: document.documentElement.scrollWidth - viewportWidth
};

JSON.stringify({ scrollInfo, overflowingElementsCount: overflowingElements.length, first20: overflowingElements.slice(0, 20) }, null, 2);

// Get unique parent containers that are actually causing the overflow (not just children inheriting it)
const viewportWidth = document.documentElement.clientWidth;
const culprits = [];

document.querySelectorAll('*').forEach(el => {
  const rect = el.getBoundingClientRect();
  if (rect.right > viewportWidth + 1) {
    const parent = el.parentElement;
    const parentRect = parent ? parent.getBoundingClientRect() : null;
    const parentOverflows = parentRect && parentRect.right > viewportWidth + 1;

    // Only report elements whose parent does NOT overflow (i.e., this element is the root cause)
    if (!parentOverflows) {
      const styles = window.getComputedStyle(el);
      culprits.push({
        tag: el.tagName,
        id: el.id,
        class: el.className?.toString?.()?.substring(0, 150),
        right: Math.round(rect.right),
        width: Math.round(rect.width),
        parentTag: parent?.tagName,
        parentClass: parent?.className?.toString?.()?.substring(0, 100),
        parentOverflow: parent ? window.getComputedStyle(parent).overflow : 'N/A',
        parentOverflowX: parent ? window.getComputedStyle(parent).overflowX : 'N/A',
      });
    }
  }
});

JSON.stringify(culprits, null, 2);

// Check overflow settings on html and body
const html = document.documentElement;
const body = document.body;
const htmlStyles = window.getComputedStyle(html);
const bodyStyles = window.getComputedStyle(body);

// Also check main/wrapper elements
const main = document.querySelector('main');
const mainStyles = main ? window.getComputedStyle(main) : null;

// Check all root-level culprits more carefully - check their actual scroll contribution
// by temporarily hiding them and checking scrollWidth
const viewportWidth = html.clientWidth;

const result = {
  html: {
    overflow: htmlStyles.overflow,
    overflowX: htmlStyles.overflowX,
    scrollWidth: html.scrollWidth,
    clientWidth: html.clientWidth,
  },
  body: {
    overflow: bodyStyles.overflow,
    overflowX: bodyStyles.overflowX,
    scrollWidth: body.scrollWidth,
    clientWidth: body.clientWidth,
    width: bodyStyles.width,
    maxWidth: bodyStyles.maxWidth,
  },
  main: main ? {
    overflow: mainStyles.overflow,
    overflowX: mainStyles.overflowX,
    scrollWidth: main.scrollWidth,
    clientWidth: main.clientWidth,
  } : null,
};

JSON.stringify(result, null, 2);

