// Identify the exact section causing overflow by testing each one
const sections = document.querySelectorAll('body > *, main > *, main > * > *');
const results = [];
const originalScrollWidth = document.documentElement.scrollWidth;
const viewportWidth = document.documentElement.clientWidth;

// Test each direct child section
const allSections = document.querySelectorAll('section, header, footer, nav, div.container, div[class*="section"]');

for (const section of allSections) {
  const origDisplay = section.style.display;
  section.style.display = 'none';
  const newScrollWidth = document.documentElement.scrollWidth;
  section.style.display = origDisplay;

  if (newScrollWidth < originalScrollWidth) {
    results.push({
      tag: section.tagName,
      class: section.className?.toString()?.substring(0, 150),
      id: section.id,
      reducedScrollWidthTo: newScrollWidth,
      savedPixels: originalScrollWidth - newScrollWidth
    });
  }
}

JSON.stringify({ originalScrollWidth, viewportWidth, overflow: originalScrollWidth - viewportWidth, fixingSections: results }, null, 2);

