// Get more details about the "relative py-28" section and find what inside it overflows
const section = document.querySelector('section.relative.py-28');
const sectionRect = section.getBoundingClientRect();
const viewportWidth = document.documentElement.clientWidth;

// Check all children of this section
const children = section.querySelectorAll('*');
const overflowChildren = [];

for (const child of children) {
  const rect = child.getBoundingClientRect();
  if (rect.right > viewportWidth + 1) {
    overflowChildren.push({
      tag: child.tagName,
      class: child.className?.toString()?.substring(0, 200),
      right: Math.round(rect.right),
      width: Math.round(rect.width),
      overflowBy: Math.round(rect.right - viewportWidth),
      parentClass: child.parentElement?.className?.toString()?.substring(0, 100),
      parentOverflow: window.getComputedStyle(child.parentElement).overflow,
    });
  }
}

// Also check what this section is - get its HTML structure (outer only)
const sectionInfo = {
  outerHTML: section.outerHTML.substring(0, 300),
  sectionOverflow: window.getComputedStyle(section).overflow,
  sectionOverflowX: window.getComputedStyle(section).overflowX,
  sectionScrollWidth: section.scrollWidth,
  sectionClientWidth: section.clientWidth,
};

JSON.stringify({ sectionInfo, overflowChildrenCount: overflowChildren.length, overflowChildren: overflowChildren.slice(0, 10) }, null, 2);

// Hide the features section and find what else is overflowing
const featuresSection = document.querySelector('section#features');
featuresSection.style.display = 'none';

const remainingScrollWidth = document.documentElement.scrollWidth;
const viewportWidth = document.documentElement.clientWidth;

// Now find all remaining overflowing elements
const allEls = document.querySelectorAll('*');
const remaining = [];

for (const el of allEls) {
  const rect = el.getBoundingClientRect();
  if (rect.right > viewportWidth + 2) {
    const parent = el.parentElement;
    const parentRect = parent?.getBoundingClientRect();
    const parentOverflows = parentRect && parentRect.right > viewportWidth + 2;

    if (!parentOverflows) {
      remaining.push({
        tag: el.tagName,
        class: el.className?.toString()?.substring(0, 200),
        right: Math.round(rect.right),
        overflowBy: Math.round(rect.right - viewportWidth),
        parentTag: parent?.tagName,
        parentClass: parent?.className?.toString()?.substring(0, 150),
        parentOverflow: parent ? window.getComputedStyle(parent).overflow : 'n/a',
      });
    }
  }
}

featuresSection.style.display = '';

JSON.stringify({ remainingScrollWidth, viewportWidth, remainingOverflow: remainingScrollWidth - viewportWidth, rootCauses: remaining }, null, 2);

// The key issue: parents have overflow:hidden but elements still overflow the page.
// This means overflow:hidden isn't actually working. Check if parent widths exceed viewport.
const viewportWidth = document.documentElement.clientWidth;

// Check the hero section (first root cause)
const heroSection = document.querySelector('section.relative.overflow-hidden');
if (heroSection) {
  const heroRect = heroSection.getBoundingClientRect();
  const heroStyles = window.getComputedStyle(heroSection);

  // Check the 900px wide glow div
  const glowDiv = heroSection.querySelector('.w-\\[900px\\]');
  const glowRect = glowDiv?.getBoundingClientRect();

  // Check the img with w-[140%]
  const img = heroSection.querySelector('img.max-w-none');
  const imgRect = img?.getBoundingClientRect();
  const imgParent = img?.parentElement;
  const imgParentRect = imgParent?.getBoundingClientRect();

  // Check the btn-tv noise span  
  const btnTv = document.querySelector('.btn-tv');
  const tvNoise = btnTv?.querySelector('.tv-noise');
  const btnRect = btnTv?.getBoundingClientRect();
  const noiseRect = tvNoise?.getBoundingClientRect();

  var info = {
    heroSection: {
      width: Math.round(heroRect.width),
      right: Math.round(heroRect.right),
      overflow: heroStyles.overflow,
      overflowX: heroStyles.overflowX,
      position: heroStyles.position,
    },
    glowDiv: glowDiv ? { width: Math.round(glowRect.width), right: Math.round(glowRect.right), left: Math.round(glowRect.left) } : null,
    img: img ? { width: Math.round(imgRect.width), right: Math.round(imgRect.right) } : null,
    imgParent: imgParent ? { 
      width: Math.round(imgParentRect.width), 
      right: Math.round(imgParentRect.right),
      overflow: window.getComputedStyle(imgParent).overflow,
      position: window.getComputedStyle(imgParent).position,
    } : null,
    btnTv: btnTv ? { width: Math.round(btnRect.width), right: Math.round(btnRect.right) } : null,
    tvNoise: tvNoise ? { width: Math.round(noiseRect.width), right: Math.round(noiseRect.right) } : null,
  };
}

JSON.stringify(info, null, 2);

