// More precise: for each direct child of body and main, hide it and check scrollWidth reduction
const viewportWidth = document.documentElement.clientWidth;
const originalSW = document.documentElement.scrollWidth;

const results = [];

// Check body direct children
const bodyChildren = document.body.children;
for (let i = 0; i < bodyChildren.length; i++) {
  const child = bodyChildren[i];
  const origDisplay = child.style.display;
  child.style.display = 'none';
  const newSW = document.documentElement.scrollWidth;
  child.style.display = origDisplay;

  if (newSW < originalSW) {
    results.push({
      level: 'body-child',
      index: i,
      tag: child.tagName,
      class: child.className?.toString()?.substring(0, 100),
      reducedTo: newSW,
      saved: originalSW - newSW
    });
  }
}

// If main exists, also check its children
const main = document.querySelector('main');
if (main) {
  const mainChildren = main.children;
  for (let i = 0; i < mainChildren.length; i++) {
    const child = mainChildren[i];
    const origDisplay = child.style.display;
    child.style.display = 'none';
    const newSW = document.documentElement.scrollWidth;
    child.style.display = origDisplay;

    if (newSW < originalSW) {
      results.push({
        level: 'main-child',
        index: i,
        tag: child.tagName,
        class: child.className?.toString()?.substring(0, 100),
        id: child.id,
        reducedTo: newSW,
        saved: originalSW - newSW
      });
    }
  }
}

JSON.stringify({ originalSW, viewportWidth, results }, null, 2);

