// Check ALL main children individually for overflow contribution
const main = document.querySelector('main');
const viewportWidth = document.documentElement.clientWidth;
const originalSW = document.documentElement.scrollWidth;
const results = [];

for (let i = 0; i < main.children.length; i++) {
  const child = main.children[i];
  const origDisplay = child.style.display;
  child.style.display = 'none';
  const newSW = document.documentElement.scrollWidth;
  child.style.display = origDisplay;

  results.push({
    index: i,
    tag: child.tagName,
    class: child.className?.toString()?.substring(0, 120),
    id: child.id || '',
    scrollWidthWhenHidden: newSW,
    saves: originalSW - newSW,
    isOverflowing: newSW < originalSW
  });
}

JSON.stringify({ originalSW, viewportWidth, totalChildren: main.children.length, results }, null, 2);

// Check ALL body direct children
const body = document.body;
const viewportWidth = document.documentElement.clientWidth;
const originalSW = document.documentElement.scrollWidth;
const results = [];

for (let i = 0; i < body.children.length; i++) {
  const child = body.children[i];
  const origDisplay = child.style.display;
  const origVis = child.style.visibility;
  child.style.display = 'none';
  const newSW = document.documentElement.scrollWidth;
  child.style.display = origDisplay;

  results.push({
    index: i,
    tag: child.tagName,
    class: child.className?.toString()?.substring(0, 120),
    id: child.id || '',
    scrollWidthWhenHidden: newSW,
    saves: originalSW - newSW,
  });
}

JSON.stringify({ originalSW, viewportWidth, totalBodyChildren: body.children.length, results }, null, 2);

// The individual children test showed 0 saves - this likely means a scrollbar or 
// something is adding width when content is present. Let me try a different approach:
// add overflow-x: hidden to different levels and see what fixes it

// First, let me check what happens with overflow:hidden on body
document.body.style.overflowX = 'hidden';
const sw1 = document.documentElement.scrollWidth;
document.body.style.overflowX = '';

// Check with overflow-x: hidden on html
document.documentElement.style.overflowX = 'hidden';
const sw2 = document.documentElement.scrollWidth;
document.documentElement.style.overflowX = '';

// Check with overflow-x: hidden on main
const main = document.querySelector('main');
main.style.overflowX = 'hidden';
const sw3 = document.documentElement.scrollWidth;
main.style.overflowX = '';

// Now let me try to find elements with computed width > viewport
const viewportWidth = document.documentElement.clientWidth;
const wideElements = [];
document.querySelectorAll('*').forEach(el => {
  const style = window.getComputedStyle(el);
  const rect = el.getBoundingClientRect();
  // Check if the element's scrollWidth exceeds its clientWidth
  if (el.scrollWidth > el.clientWidth + 2 && el.scrollWidth > viewportWidth) {
    wideElements.push({
      tag: el.tagName,
      class: el.className?.toString()?.substring(0, 120),
      scrollWidth: el.scrollWidth,
      clientWidth: el.clientWidth,
      overflow: style.overflow,
      overflowX: style.overflowX,
    });
  }
});

JSON.stringify({ 
  withBodyOverflowHidden: sw1,
  withHtmlOverflowHidden: sw2,
  withMainOverflowHidden: sw3,
  viewportWidth,
  wideElements 
}, null, 2);

// Now let me find EXACTLY which sections inside main have scrollWidth > clientWidth
const main = document.querySelector('main');
const results = [];

for (let i = 0; i < main.children.length; i++) {
  const child = main.children[i];
  if (child.tagName === 'SCRIPT') continue;

  const style = window.getComputedStyle(child);
  results.push({
    index: i,
    tag: child.tagName,
    class: child.className?.toString()?.substring(0, 120),
    id: child.id || '',
    scrollWidth: child.scrollWidth,
    clientWidth: child.clientWidth,
    overflowX: style.overflowX,
    overflows: child.scrollWidth > child.clientWidth + 2,
    overflowAmount: child.scrollWidth - child.clientWidth,
  });
}

JSON.stringify(results, null, 2);

// Get full results including which sections overflow
const main = document.querySelector('main');
const results = [];

for (let i = 0; i < main.children.length; i++) {
  const child = main.children[i];
  if (child.tagName === 'SCRIPT') continue;

  const style = window.getComputedStyle(child);
  const entry = {
    index: i,
    tag: child.tagName,
    class: child.className?.toString()?.substring(0, 150),
    id: child.id || '',
    scrollWidth: child.scrollWidth,
    clientWidth: child.clientWidth,
    overflowX: style.overflowX,
    overflowAmount: child.scrollWidth - child.clientWidth,
  };

  // For sections that overflow, find the immediate culprit children
  if (child.scrollWidth > child.clientWidth + 2) {
    const culprits = [];
    for (const subChild of child.querySelectorAll('*')) {
      const rect = subChild.getBoundingClientRect();
      const childRect = child.getBoundingClientRect();
      if (rect.right > childRect.right + 2) {
        const parent = subChild.parentElement;
        const parentRect = parent.getBoundingClientRect();
        if (parentRect.right <= childRect.right + 2 || parent === child) {
          culprits.push({
            tag: subChild.tagName,
            class: subChild.className?.toString()?.substring(0, 150),
            overflowBy: Math.round(rect.right - childRect.right),
          });
        }
      }
    }
    entry.culprits = culprits.slice(0, 5);
  }

  results.push(entry);
}

JSON.stringify(results.filter(r => r.overflowAmount > 0), null, 2);

