      width: Math.round(rect.width),
      parentClass: child.parentElement?.className?.toString()?.substring(0, 100),
      parentOverflow: window.getComputedStyle(child.parentElement).overflow,
    });
  }
}

// Also get the last section (index 7)
const allSections = document.querySelectorAll('main > section');
const lastSection = allSections[allSections.length - 1];
const lastCulprits = [];
const lastRect = lastSection.getBoundingClientRect();

for (const child of lastSection.querySelectorAll('*')) {
  const rect = child.getBoundingClientRect();
  if (rect.right > lastRect.right + 2) {
    const parent = child.parentElement;
    const parentRect = parent.getBoundingClientRect();
    if (parentRect.right <= lastRect.right + 2 || parent === lastSection) {
      lastCulprits.push({
        tag: child.tagName,
        class: child.className?.toString()?.substring(0, 200),
        overflowBy: Math.round(rect.right - lastRect.right),
      });
    }
  }
}

JSON.stringify({
  downloadCulprits: culprits.slice(0, 5),
  lastSectionClass: lastSection.className,
  lastSectionId: lastSection.id,
  lastCulprits: lastCulprits.slice(0, 5)
}, null, 2);

