// Check what causes 500px scrollWidth when main is hidden (6px overflow)
const main = document.querySelector('main');
main.style.display = 'none';

// 500px scrollWidth - find what's causing it
const viewportWidth = document.documentElement.clientWidth; // 494
const remaining = [];

document.querySelectorAll('body > *:not(main)').forEach(el => {
  if (el.style.display === 'none') return;
  const rect = el.getBoundingClientRect();
  if (rect.width > 0) {
    remaining.push({
      tag: el.tagName,
      class: el.className?.toString()?.substring(0, 100),
      width: Math.round(rect.width),
      right: Math.round(rect.right),
      scrollWidth: el.scrollWidth,
    });
  }
});

// Also check footer children
const footer = document.querySelector('footer');
const footerCulprits = [];
if (footer) {
  for (const child of footer.querySelectorAll('*')) {
    const rect = child.getBoundingClientRect();
    if (rect.right > viewportWidth + 1) {
      footerCulprits.push({
        tag: child.tagName,
        class: child.className?.toString()?.substring(0, 150),
        right: Math.round(rect.right),
        overflowBy: Math.round(rect.right - viewportWidth),
      });
    }
  }
}

main.style.display = '';

JSON.stringify({ 
  scrollWidthWithoutMain: document.documentElement.scrollWidth,
  viewportWidth, 
  bodyChildren: remaining,
  footerCulprits: footerCulprits.slice(0, 5)
}, null, 2);

