file.content:
---
import BaseHead from '../components/BaseHead.astro';
import Header from '../components/Header.astro';
import Footer from '../components/Footer.astro';
import '../styles/global.css';

interface Props {
  title: string;
  description: string;
  image?: string;
  type?: 'website' | 'article';
  noindex?: boolean;
  keywords?: string[];
  publishedTime?: string;
  modifiedTime?: string;
  jsonLd?: Record<string, unknown> | Array<Record<string, unknown>>;
}

const { title, description, image, type, noindex, keywords, publishedTime, modifiedTime, jsonLd } = Astro.props;
---

<!doctype html>
<html lang="en" class="dark">
  <head>
    <BaseHead
      title={title}
      description={description}
      image={image}
      type={type}
      noindex={noindex}
      keywords={keywords}
      publishedTime={publishedTime}
      modifiedTime={modifiedTime}
      jsonLd={jsonLd}
    />
  </head>
  <body class="min-h-screen bg-surface-950 font-body text-surface-200">
    <Header />
    <main>
      <slot />
    </main>
    <Footer />
  </body>
</html>

---
import { getCollection } from 'astro:content';
import BaseLayout from '../layouts/BaseLayout.astro';
import Hero from '../components/Hero.astro';
import FeatureGrid from '../components/FeatureGrid.astro';
import ScreenshotShowcase from '../components/ScreenshotShowcase.astro';
import DownloadCTA from '../components/DownloadCTA.astro';
import SupportSection from '../components/SupportSection.astro';
import FeaturedBlogPost from '../components/FeaturedBlogPost.astro';
import BlogPostCard from '../components/BlogPostCard.astro';

const includeDrafts = import.meta.env.DEV || import.meta.env.PUBLIC_INCLUDE_DRAFTS === 'true';

const allPosts = (await getCollection('blog'))
  .filter((post) => includeDrafts || !post.data.draft)
  .sort((a, b) => b.data.pubDate.valueOf() - a.data.pubDate.valueOf());

const featuredPost = allPosts.find((post) => post.data.featured) ?? allPosts[0];
const otherPosts = featuredPost ? allPosts.filter((post) => post.id !== featuredPost.id).slice(0, 2) : [];
const posts = featuredPost ? [featuredPost, ...otherPosts] : allPosts.slice(0, 3);

const homeSchema = [
  {
    '@context': 'https://schema.org',
    '@type': 'WebSite',
    name: 'IPTVnator',
    url: 'https://4gray.github.io/iptvnator/',
    description:
      'A cross-platform IPTV player supporting M3U/M3U8 playlists, Xtream Codes API, and Stalker portals.',
    inLanguage: 'en',
  },
  {
    '@context': 'https://schema.org',
    '@type': 'SoftwareApplication',
    name: 'IPTVnator',
    applicationCategory: 'MultimediaApplication',
    operatingSystem: 'Windows, macOS, Linux, Web',
    softwareVersion: '0.19.0',
    url: 'https://4gray.github.io/iptvnator/',
    downloadUrl: 'https://github.com/4gray/iptvnator/releases/latest',
    author: {
      '@type': 'Person',
      name: '4gray',
      url: 'https://github.com/4gray',
    },
    sameAs: [
      'https://github.com/4gray/iptvnator',
      'https://t.me/iptvnator',
      'https://ko-fi.com/4gray',
    ],
    description:
      'Open-source IPTV player with M3U/M3U8, Xtream Codes API, and Stalker portal support.',
  },
];
---

<BaseLayout
  title="IPTVnator - Open Source IPTV Player"
  description="A cross-platform IPTV player supporting M3U/M3U8 playlists, Xtream Codes API, and Stalker portals. Available as a desktop app and PWA."
  keywords={['IPTV player', 'M3U', 'M3U8', 'Xtream Codes', 'Stalker portal', 'EPG', 'XMLTV', 'open-source']}
  jsonLd={homeSchema}
>
  <Hero />
  <FeatureGrid />
  <ScreenshotShowcase />
  <DownloadCTA />
  <SupportSection />

  <!-- Recent Blog Posts -->
  {
    posts.length > 0 && (
      <section class="py-28">
        <hr class="section-divider mb-28" />

        <div class="mx-auto max-w-6xl px-6">
          <div class="flex items-end justify-between">
            <div>
              <span class="font-mono text-xs uppercase tracking-[0.2em] text-accent-500">
                Blog
              </span>
              <h2 class="mt-4 font-display text-3xl font-bold tracking-tight text-surface-50 sm:text-4xl lg:text-5xl">
                Latest <em class="font-medium text-accent-400" style="font-style: italic;">updates</em>
              </h2>
              <p class="mt-4 text-lg text-surface-400">
                Release notes, tutorials, and project updates.
              </p>
            </div>
            <a
              href="/iptvnator/blog/"
              class="hidden items-center gap-1.5 font-mono text-xs uppercase tracking-[0.1em] text-accent-500 transition-colors hover:text-accent-400 sm:inline-flex"
            >
              View all posts
              <svg class="h-3.5 w-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
                <path stroke-linecap="round" stroke-linejoin="round" d="M17 8l4 4m0 0l-4 4m4-4H3" />
              </svg>
            </a>
          </div>

          {/* Featured post */}
          {featuredPost && (
            <div class="mt-12">
              <FeaturedBlogPost
                title={featuredPost.data.title}
                description={featuredPost.data.description}
                pubDate={featuredPost.data.pubDate}
                slug={featuredPost.id}
                tags={featuredPost.data.tags}
                heroImage={featuredPost.data.heroImage}
              />
            </div>
          )}

          {/* Other posts grid */}
          {otherPosts.length > 0 && (
            <div class="mt-8 grid gap-5 sm:grid-cols-2 lg:grid-cols-3">
              {otherPosts.map((post) => (
                <BlogPostCard
                  title={post.data.title}
                  description={post.data.description}
                  pubDate={post.data.pubDate}
                  slug={post.id}
                  tags={post.data.tags}
                  heroImage={post.data.heroImage}
                />
              ))}
            </div>
          )}
        </div>
      </section>
    )
  }
</BaseLayout>

