 66→  }
 67→
 68→  /* ── TV-effect download button ── */
 69→  .btn-tv {
 70→    position: relative;
 71→    display: inline-flex;
 72→    overflow: hidden;
 73→    isolation: isolate;
 74→    border: 1px solid rgba(32, 168, 168, 0.3);
 75→    background: linear-gradient(135deg, rgba(32, 168, 168, 0.12) 0%, rgba(32, 168, 168, 0.06) 50%, rgba(32, 168, 168, 0.1) 100%);
 76→    background-size: 180% 180%;
