 82→      inset 0 0 20px rgba(32, 168, 168, 0.03);
 83→    transition: all 0.4s cubic-bezier(0.16, 1, 0.3, 1);
 84→    animation: tvIdleGlow 2.1s ease-in-out infinite, tvSurfaceShift 2.8s linear infinite, tvButtonJitter 1.2s steps(2, end) infinite;
 85→    will-change: transform, box-shadow, background-position;
 86→  }
 87→
 88→  .btn-tv::before {
 89→    content: '';
 90→    position: absolute;
 91→    inset: 0;
 92→    z-index: 1;
 93→    opacity: 0.34;
 94→    background: repeating-linear-gradient(
 95→      0deg,
 96→      transparent,
 97→      transparent 2px,
 98→      rgba(32, 168, 168, 0.03) 2px,
 99→      rgba(32, 168, 168, 0.03) 4px
100→    );
101→    transition: opacity 0.3s ease;
102→    pointer-events: none;
103→    animation: tvHorizontalRoll 2.2s linear infinite;
104→  }
105→
106→  .btn-tv::after {
107→    content: '';
108→    position: absolute;
109→    inset: 0;
110→    z-index: 1;
111→    opacity: 0.22;
112→    pointer-events: none;
113→    mix-blend-mode: screen;
114→    background-image:
115→      linear-gradient(180deg, rgba(32, 168, 168, 0) 0%, rgba(32, 168, 168, 0.1) 48%, rgba(32, 168, 168, 0) 100%),
116→      repeating-linear-gradient(
117→        0deg,
118→        transparent 0px,
119→        transparent 5px,
