const html = document.documentElement;
const viewportWidth = html.clientWidth;
const scrollWidth = html.scrollWidth;
const bodyScrollWidth = document.body.scrollWidth;
const mainEl = document.querySelector('main');
const mainScrollWidth = mainEl ? mainEl.scrollWidth : 'N/A';
const mainOverflowX = mainEl ? window.getComputedStyle(mainEl).overflowX : 'N/A';

JSON.stringify({
  viewportWidth,
  htmlScrollWidth: scrollWidth,
  bodyScrollWidth,
  mainScrollWidth,
  mainOverflowX,
  hasHorizontalScroll: scrollWidth > viewportWidth,
  overflowAmount: scrollWidth - viewportWidth
}, null, 2);

const html = document.documentElement;
const body = document.body;
const viewportWidth = html.clientWidth;

JSON.stringify({
  viewportWidth,
  htmlScrollWidth: html.scrollWidth,
  bodyScrollWidth: body.scrollWidth,
  hasHorizontalScroll: html.scrollWidth > viewportWidth,
  overflowAmount: html.scrollWidth - viewportWidth,
  mainOverflowX: window.getComputedStyle(document.querySelector('main')).overflowX,
  downloadSectionOverflow: window.getComputedStyle(document.querySelector('#download')).overflow,
}, null, 2);
